import React from "react";
import { collection, onSnapshot, query, where } from "firebase/firestore";
import { db } from "../Firebase/config";

const useFirebase = (collectionName, condition) => {
  const [document, setDocument] = React.useState([]);
  React.useEffect(() => {
    const roomCollectionRef = collection(db, collectionName);

    const queryData = query(
      roomCollectionRef,
      where(condition.field, condition.operator, condition.valueComparison)
    );
    const unsubcribe = onSnapshot(queryData, (docsSnap) => {
      console.log(docsSnap);
      setDocument(docsSnap);
    });
    return () => {
      unsubcribe();
    };
  }, [collectionName, condition]);
  return document;
};

export default useFirebase;
