import {
  AntDesignOutlined,
  SendOutlined,
  UserAddOutlined,
  UserOutlined,
} from "@ant-design/icons";
import { Avatar, Button, Input, Tooltip, Typography } from "antd";
import React from "react";
import styled from "styled-components";

const StyleAvatarWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 30px;
  border-bottom: 1px solid #ccc;
  &&& {
    .ant-btn {
      background-color: #391898;
      width: 50px;
      height: 50px;
      border-radius: 100rem;
    }
  }
`;

const StyleSend = styled.div`
  position: fixed;
  bottom: 0px;
  justify-content: center;
  width: 80%;
  gap: 15px;
  display: flex;
  padding: 10px;
  font-size: 50px;
  border: 1px solid #ccc;
  &&& {
    .ant-btn.ant-btn-circle {
      width: 50px;
      height: 50px;
      transform: translateY(3px);
      background-color: #391898;
    }
    .ant-input {
      font-size: 0.4em;
      width: 60%;
      height: 60px;
      border-radius: 20px;
    }
  }
`;
const StyleAvatar = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 50px;
  margin-left: 30px;
  &&& {
    .ant-typography {
      font-size: 2em;
      font-weight: bold;
    }
  }
`;
const StyleChatbox = styled.div`
  padding: 50px 60px;
`;

const ChatBox = () => {
  return (
    <div>
      <StyleAvatarWrapper>
        <StyleAvatar>
          <Typography.Text>Chat room</Typography.Text>
        </StyleAvatar>
        <Button type="primary" icon={<UserAddOutlined />} size="large" />

        <Avatar.Group
          maxCount={2}
          size="large"
          maxStyle={{ color: "#f56a00", backgroundColor: "#fde3cf" }}
        >
          <Tooltip title="Nguyệt Quỳnh" placement="top">
            <Avatar src="https://lh3.googleusercontent.com/pw/AL9nZEUF_s0N1sZh6Cs742iF3WRUpLWs4ds-ddm8juJL3GarAgGvrymm1d62MmJhKL9EX6ugGpeQYu7SvEpyzdYI3QOPuDVeOQGsU1JNa5mNCfKT_AjTeHdC5lS16MP2vv34aatg4k3dGyBfZwgQ4v5RkFPa=w700-h933-no?authuser=0" />
          </Tooltip>
          <Tooltip title="Nguyệt Quỳnh" placement="top">
            <Avatar style={{ backgroundColor: "#f56a00" }}>Q</Avatar>
          </Tooltip>
          <Tooltip title="Ant User" placement="top">
            <Avatar
              style={{ backgroundColor: "#87d068" }}
              icon={<UserOutlined />}
            />
          </Tooltip>
          <Avatar
            style={{ backgroundColor: "#1890ff" }}
            icon={<AntDesignOutlined />}
          />
        </Avatar.Group>
      </StyleAvatarWrapper>
      <StyleChatbox>Chat box</StyleChatbox>
      <StyleSend>
        <Input placeholder="Nhập tin nhắn..." />
        <Button
          type="primary"
          shape="circle"
          icon={<SendOutlined />}
          size={60}
        />
      </StyleSend>
    </div>
  );
};

export default ChatBox;
