import React from "react";
import { Collapse, Typography, Button } from "antd";
import styled from "styled-components";
import { PlusOutlined } from "@ant-design/icons";
import useFirebase from "../../../hooks/useFirebase";
import { AuthContext } from "../../../Context/AuthProvider";

const StylePanel = styled(Collapse.Panel)`
  &&& {
    .ant-collapse-header {
      color: white;
      font-size: 16px;
      padding: 20px 30px 0 30px;
    }
    .ant-collapse-content-box {
      padding: 10px 50px;
    }
    .add-room {
      padding: 0;
      color: white;
    }
  }
`;
const StyleTypo = styled(Typography.Link)`
  display: block;
  color: black;
  margin-bottom: 20px;
`;

const ChatList = () => {
  /*
    rooms {
      name: 
      member : [uid1, uid2]
    }
  */

  return (
    <Collapse ghost defaultActiveKey={["1"]}>
      <StylePanel header="Danh sách các phòng" key="1">
        <StyleTypo className="Room">Chat List</StyleTypo>
        <StyleTypo className="Room">Chat Box</StyleTypo>
        <StyleTypo className="Room">Hello</StyleTypo>
        <Button className="add-room" type="text" ghost icon={<PlusOutlined />}>
          Thêm một phòng
        </Button>
      </StylePanel>
    </Collapse>
  );
};

export default ChatList;
