import {
  AntDesignOutlined,
  SendOutlined,
  UserOutlined,
} from "@ant-design/icons";
import { Avatar, Button, Input, Tooltip, Typography } from "antd";
import React from "react";
import styled from "styled-components";

const StyleAvatarWrapper = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 30px;
  border-bottom: 1px solid #ccc;
`;

const StyleSend = styled.div`
  position: fixed;
  bottom: 0px;
  justify-content: center;
  width: 80%;
  gap: 10px;
  display: flex;
  padding: 10px;
  background-color: #7950ef;
  font-size: 50px;
  &&& {
    .ant-btn.ant-btn-circle {
      width: 50px;
      height: 50px;
      transform: translateY(3px);
    }
    .ant-input {
      font-size: 0.4em;
      width: 80%;
      height: 60px;
      border-radius: 20px;
    }
  }
`;
const StyleAvatar = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 20px;
  &&& {
    .ant-typography {
      font-size: 1.2em;
    }
  }
`;

const ChatBox = () => {
  return (
    <div>
      <StyleAvatarWrapper>
        <StyleAvatar>
          <Avatar size="large">Q</Avatar>
          <Typography.Text>Chat room</Typography.Text>
        </StyleAvatar>
        <Avatar.Group
          maxCount={3}
          size="large"
          maxStyle={{ color: "#f56a00", backgroundColor: "#fde3cf" }}
        >
          <Avatar src="https://lh3.googleusercontent.com/EKuPEsJM8-396CqndFx90ZCT10WWEutoYLyjf7mVhrwxFsIYfkWhxilhex0u7BxOx8jt00IPk9yfi6WDr0g3duz6CJWskE7MOWmw7WraC1bKyA4oPndCHpPStopZkvz63ftd60Cn5XoAOQRP41f7ASZIoy2Sd-Vqw8Y3EX3xeEHKApbUc5anwO7Or-CZq9vipo_8F5NCDIQ-8q5YcT7ai4EFqEBCZ_dIEXtKdv4mqKZnaOEDCcedoOH718BNTZpIHt_2IzpgH7ModeT2XzKkMvsHT5fqoMSUX2I-R3nyVGhAxaiXGtDdQeBpAkUKMEdoKsRPvv7oQSdtk3XiE8xXps23OVTTQEGW3EkRaJJxzuqnj8qXoTyLMilugY4FgRJHfcqQOZgDzTg-tYPl_2kcuYA41w1-CMKrexYQFMb-xukYFffx0M71_Rn-26w5BGzWBWizqCCiX0JMtV4i1wU-SEeZgzv4y761Oxf_zQepUAchAXz0EGPmAcwDR61NM3ZSObJR8gkPb06uzrDu6AymLEVdYXRUqm4Z5Vi8bww-enFjHCRnzfVgz2SGe1TafwFw0kOnkqsCVLaAsen-B6ORMgf4gslz_ls7I-NRRHuqAAKSnyiEFfYMdsVfyCH6Xts6NCT-FB-YUIg5ISaJA6gaJFcS1XHbWRytL6ptIs-J5d1JYu42JETa9UC9ATxu6tuo7r9UFTgjt9hbS7k1DUWKMzaYE3GfO4eeAKqKxskOouGKhVd9u0BGZDkszqYoZ6wKFejEqjV-_Qmd6upBK2BZxHUuo_LgRl61fG61MLQABG_ZmqW4uisZFXg8pzSJacxYu3XdxrQVqBM97IBmSzMHs1xUN7xeOT_UZN0kie4b2kENWjBf40zlfyo6-zXIB-8KmJxoGn4p-GAowLXlH2BhDGx2ExA7LIBP9weYaZk7_9NWljBiOVBEQVW61gYjtsj6eNaBZ7S2_BI70rVM_If7DWMxJF9VYIDESC7tu_dNxofqJMpq0lzpi-kVBGpzl1UblzHguW_He0YyjQ3L8UKtosXzFF3k2xGr2s52KqqLnBfLm6xTEzKExdtzVNSB5z1YWUjbJYKyjBcOffy_ld62rICfv7MK_O96Y5AuW6I9CUzxQuSF5wF_-I0L7xhJY1sSqw=w624-h933-no?authuser=3" />
          <Avatar style={{ backgroundColor: "#f56a00" }}>K</Avatar>
          <Tooltip title="Ant User" placement="top">
            <Avatar
              style={{ backgroundColor: "#87d068" }}
              icon={<UserOutlined />}
            />
          </Tooltip>
          <Avatar
            style={{ backgroundColor: "#1890ff" }}
            icon={<AntDesignOutlined />}
          />
        </Avatar.Group>
      </StyleAvatarWrapper>
      <div>Chat box</div>
      <StyleSend>
        <Input placeholder="Nhập tin nhắn..." />
        <Button
          type="primary"
          shape="circle"
          icon={<SendOutlined />}
          size={60}
        />
      </StyleSend>
    </div>
  );
};

export default ChatBox;
