import React, { useContext } from "react";
import { Collapse, Typography, Button } from "antd";
import styled from "styled-components";
import { PlusOutlined } from "@ant-design/icons";
import { AuthContext } from "../../../Context/AuthProvider";
import { useFirestore } from "../../../hooks/useFirestore";

const StylePanel = styled(Collapse.Panel)`
  &&& {
    .ant-collapse-header {
      color: white;
      font-size: 16px;
      padding: 20px 30px 0 30px;
    }
    .ant-collapse-content-box {
      padding: 10px 50px;
    }
    .add-room {
      padding: 0;
      color: white;
    }
  }
`;
const StyleTypo = styled(Typography.Link)`
  display: block;
  color: black;
  margin-bottom: 20px;
`;

const ChatList = () => {
  const {
    user: { uid },
  } = useContext(AuthContext);

  const roomCondition = React.useMemo(() => {
    return {
      field: "members",
      operator: "array-contains",
      compareValue: "PHuyR7ebWlrilX0ZU4DThFzS4fpq",
    };
  }, []);
  const roomData = useFirestore("rooms", roomCondition);
  console.log(roomData);

  return (
    <Collapse ghost defaultActiveKey={["1"]}>
      <StylePanel header="Danh sách các phòng" key="1">
        {roomData.map((room) => (
          <StyleTypo className="Room" key={room.id}>
            {room.name}
          </StyleTypo>
        ))}
        <Button className="add-room" type="text" ghost icon={<PlusOutlined />}>
          Thêm một phòng
        </Button>
      </StylePanel>
    </Collapse>
  );
};

export default ChatList;
