import React, { useContext } from "react";
import { useFirestore } from "../../../hooks/useFirestore";
import { Collapse, Typography, Button, Modal, Input, Form } from "antd";
import styled from "styled-components";
import { PlusOutlined, UserOutlined } from "@ant-design/icons";
import FormItem from "antd/es/form/FormItem";
import { AuthContext } from "../../../Context/AuthProvider";
import { RoomContext } from "../../../Context/RoomProvider";

const StylePanel = styled(Collapse.Panel)`
  &&& {
    .ant-collapse-header {
      color: white;
      font-size: 16px;
      padding: 20px 30px 0 30px;
    }
    .ant-collapse-content-box {
      padding: 10px 50px;
    }
    .add-room {
      padding: 0;
      color: white;
    }
  }
`;
const StyleTypo = styled(Typography.Link)`
  display: block;
  color: black;
  margin-bottom: 20px;
`;

const ChatList = () => {
  const [isModalOpen, setIsModalOpen] = React.useState(false);
  // const {
  //   user: { uid },
  // } = React.useContext(AuthContext);
  // const roomCondition = React.useMemo(() => {
  //   return {
  //     field: "members",
  //     operator: "array-contains",
  //     compareValue: uid,
  //   };
  // }, [uid]);
  // const roomData = useFirestore("rooms", roomCondition);

  const { rooms } = useContext(RoomContext);

  const handleOk = () => {
    setIsModalOpen(false);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };
  const handleOpen = () => {
    form.setFieldsValue({});
    setIsModalOpen(true);
  };
  const [form] = Form.useForm();
  const nameValue = Form.useWatch("room", form);
  console.log(nameValue);
  return (
    <>
      <Modal
        title="Thêm một phòng"
        open={isModalOpen}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <Form layout="vertical" form={form}>
          <FormItem
            label="Tên phòng"
            name="room"
            rules={[
              {
                message: "Needed name!",
              },
            ]}
          >
            <Input
              size="large"
              placeholder="Tên phòng"
              prefix={<UserOutlined />}
            />
          </FormItem>
        </Form>
      </Modal>

      <Collapse ghost defaultActiveKey={["1"]}>
        <StylePanel header="Danh sách các phòng" key="1">
          {rooms.map((room) => (
            <StyleTypo className="Room" key={room.id}>
              {room.name}
            </StyleTypo>
          ))}
          <Button
            className="add-room"
            type="text"
            ghost
            icon={<PlusOutlined />}
            onClick={handleOpen}
          >
            Thêm một phòng
          </Button>
        </StylePanel>
      </Collapse>
    </>
  );
};

export default ChatList;
