import React from "react";
import { useFirestore } from "../../../hooks/useFirestore";
import { Collapse, Typography, Button, Modal } from "antd";
import styled from "styled-components";
import { PlusOutlined } from "@ant-design/icons";

const StylePanel = styled(Collapse.Panel)`
  &&& {
    .ant-collapse-header {
      color: white;
      font-size: 16px;
      padding: 20px 30px 0 30px;
    }
    .ant-collapse-content-box {
      padding: 10px 50px;
    }
    .add-room {
      padding: 0;
      color: white;
    }
  }
`;
const StyleTypo = styled(Typography.Link)`
  display: block;
  color: black;
  margin-bottom: 20px;
`;

const ChatList = () => {
  const roomCondition = React.useMemo(() => {
    return {
      field: "members",
      operator: "array-contains",
      compareValue: "PHuyR7ebWlrilX0ZU4DThFzS4fpq",
    };
  }, []);
  const roomData = useFirestore("rooms", roomCondition);
  console.log(roomData);
  const [isModalOpen, setIsModalOpen] = React.useState(false);

  const showModal = () => {
    setIsModalOpen(true);
  };

  const handleOk = () => {
    setIsModalOpen(false);
  };

  const handleCancel = () => {
    setIsModalOpen(false);
  };
  return (
    <>
      <Modal
        title="Thêm một phòng"
        open={isModalOpen}
        onOk={handleOk}
        onCancel={handleCancel}
      >
        <p>Some contents...</p>
        <p>Some contents...</p>
        <p>Some contents...</p>
      </Modal>

      <Collapse ghost defaultActiveKey={["1"]}>
        <StylePanel header="Danh sách các phòng" key="1">
          {roomData.map((room) => (
            <StyleTypo className="Room" key={room.id}>
              {room.name}
            </StyleTypo>
          ))}
          <Button
            className="add-room"
            type="text"
            ghost
            icon={<PlusOutlined />}
            onClick={showModal}
          >
            Thêm một phòng
          </Button>
        </StylePanel>
      </Collapse>
    </>
  );
};

export default ChatList;
