import React, { useEffect } from "react";
import { Avatar, Typography, Button } from "antd";
import { signOut } from "firebase/auth";
import styled from "styled-components";
import { auth, db } from "../../../Firebase/config";
import { collection, onSnapshot } from "firebase/firestore";

const StyleUserInfo = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 32px 20px;
  gap: 2em;
  border-bottom: 1px solid #ccc;
  &&& {
    .btn-logout {
      background-color: #391898;
    }
  }
`;

const StyleAvatar = styled.div`
  display: flex;
  justify-content: space-around;
  align-items: center;
  gap: 10px;
`;
const UserInfo = () => {
  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, "users"), (docsSnap) => {
      docsSnap.forEach((doc) => {
        console.log(doc.data());
      });
    });
    unsubscribe();
  }, []);

  return (
    <StyleUserInfo>
      <StyleAvatar>
        <Avatar
          src={
            "https://lh3.googleusercontent.com/pw/AL9nZEUF_s0N1sZh6Cs742iF3WRUpLWs4ds-ddm8juJL3GarAgGvrymm1d62MmJhKL9EX6ugGpeQYu7SvEpyzdYI3QOPuDVeOQGsU1JNa5mNCfKT_AjTeHdC5lS16MP2vv34aatg4k3dGyBfZwgQ4v5RkFPa=w700-h933-no?authuser=0"
          }
          size="large"
        />
        <Typography.Text>Nguyệt Quỳnh</Typography.Text>
      </StyleAvatar>
      <Button
        type="primary"
        className="btn-logout"
        onClick={() => signOut(auth)}
      >
        Đăng xuất
      </Button>
    </StyleUserInfo>
  );
};

export default UserInfo;
