import React from "react";
import Button from "./Button/Button";
import styled from "styled-components";
import { auth } from "../../Firebase/config";
import {
  FacebookAuthProvider,
  signInWithPopup,
  getAdditionalUserInfo,
} from "firebase/auth";
const StyleDiv = styled.div`
  width: 400px;
  height: 500px;
  display: flex;
  justify-content: space-around;
  flex-direction: column;
  align-items: center;
  gap: 10px;
  border: 5px solid #0d0745;
  border-radius: 15px;
  box-shadow: rgba(0, 0, 0, 0.16) 0px 1px 4px;
  text-align: center;
  margin: auto;
  margin-top: 10vh;
`;
const StyleHeading = styled.h1`
  font-size: 3.5em;
  font-weight: bold;
`;

const providerFb = new FacebookAuthProvider();
providerFb.addScope("user_birthday");

const Login = () => {
  const handleLoginFb = async () => {
    const data = await signInWithPopup(auth, providerFb);
    const { isNewUser } = getAdditionalUserInfo(data);
    console.log({ data, isNewUser });
  };

  return (
    <StyleDiv>
      <StyleHeading>Login</StyleHeading>
      <div>
        <Button text={"Login with FaceBook"} onClick={handleLoginFb} />
        <Button text={"Login with Google"} />
      </div>
    </StyleDiv>
  );
};

export default Login;
