import { SendOutlined, UserAddOutlined, UserOutlined } from "@ant-design/icons";
import { Avatar, Button, Input, Tooltip, Typography } from "antd";
import styled from "styled-components";
import { RoomContext } from "../../Context/RoomProvider";
import Message from "./Message/Message";
import React from "react";

const StyleContainer = styled.div`
  height: 100vh;
`;
const StyleAvatarWrapper = styled.div`
  height: 105px;
  display: flex;
  justify-content: space-between;
  padding: 30px;
  border-bottom: 1px solid #ccc;
  position: relative;
  &&& {
    .ant-btn {
      background-color: #391898;
      border-radius: 100rem;
      position: absolute;
      transform: translateX(-120%);
    }
  }
`;

const StyleSend = styled.div`
  max-height: 85px;
  position: fixed;
  bottom: 0px;
  justify-content: center;
  background-color: #fff;
  width: 80%;
  gap: 15px;
  display: flex;
  padding: 10px;
  font-size: 50px;
  border: 1px solid #ccc;
  &&& {
    .ant-btn.ant-btn-circle {
      width: 50px;
      height: 50px;
      transform: translateY(3px);
      background-color: #391898;
    }
    .ant-input {
      font-size: 0.4em;
      width: 60%;
      height: 60px;
      border-radius: 20px;
    }
  }
`;
const StyleAvatar = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 50px;
  margin-left: 30px;
  &&& {
    .ant-typography {
      font-size: 2em;
      font-weight: bold;
      margin-left: 50px;
    }
  }
`;
const StyleChatbox = styled.div`
  padding: 50px 60px;
  height: calc(100% - 190px);
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  overflow-y: auto;
`;

const ChatBox = () => {
  const { sellectedRoom, userList, setIsInviteModalOpen } =
    React.useContext(RoomContext);
  return (
    <StyleContainer>
      <StyleAvatarWrapper>
        <StyleAvatar>
          <Typography.Text>{sellectedRoom.name}</Typography.Text>
        </StyleAvatar>

        <Avatar.Group
          maxCount={2}
          size="large"
          maxStyle={{ color: "#f56a00", backgroundColor: "#fde3cf" }}
        >
          <Button
            type="primary"
            icon={<UserAddOutlined />}
            size="large"
            onClick={() => setIsInviteModalOpen(true)}
          >
            Mời
          </Button>
          {userList.map((user) => (
            <Tooltip title={user.displayName} placement="top" key={user.uid}>
              <Avatar
                src={user.photoURL}
                icon={!user.photoURL ? <UserOutlined /> : null}
              />
            </Tooltip>
          ))}
        </Avatar.Group>
      </StyleAvatarWrapper>
      <StyleChatbox>
        <Message
          displayName={"Quynh"}
          photoURL={null}
          message={"Hello"}
        ></Message>
        <Message displayName={"Quynh"} photoURL={null} message={"Hi"}></Message>
        <Message
          displayName={"Quynh"}
          photoURL={null}
          message={"Hello world"}
        ></Message>
      </StyleChatbox>
      <StyleSend>
        <Input placeholder="Nhập tin nhắn..." />
        <Button
          type="primary"
          shape="circle"
          icon={<SendOutlined />}
          size={60}
        />
      </StyleSend>
    </StyleContainer>
  );
};

export default ChatBox;
